import BitMatrix from '@zxing/library/cjs/core/common/BitMatrix';
import Writer from '@zxing/library/cjs/core/Writer';
import BarcodeFormat from '@zxing/library/cjs/core/BarcodeFormat';
import EncodeHintType from '@zxing/library/cjs/core/EncodeHintType';
/**
 * This is a factory class which finds the appropriate Writer subclass for the BarcodeFormat
 * requested and encodes the barcode with the supplied contents.
 *
 * @author dswitkin@google.com (Daniel Switkin)
 */
export default class MultiFormatWriter implements Writer {
    encode(contents: string, format: BarcodeFormat, width: number, height: number, hints: Map<EncodeHintType, any>): BitMatrix;
}
